// Generated by CoffeeScript 1.10.0
(function() {
  var post_vimrc, vim_reload, vimrc_reload;

  $("#setting-btn").click(function() {

    /* 
    json形式
    {
      "connection_id": "xxxxxxxxx",
      "vimrc_contents": {
        "colorcheme": "molokai", // colorcheme molokai
        "number": true,          // set number
        "ruler": false,          // when value is false, skip
        "history": 100,          // set history=100
        "encoding": "utf-8"      // set encoding=utf-8
      }
    }
     */
    var connection_id, results;
    results = {};
    connection_id = $('#connection_id').val();
    $(".vimrc-contents:not(.genre-fixed)").map(function() {
      var key, val;
      key = $(this).attr('name');
      val;
      if (key === "colorscheme") {
        if ($(this).prop('checked')) {
          val = $(this).val();
          results[key] = val;
        }
        return;
      }
      if ($(this).attr('type') === 'checkbox') {
        if ($(this).prop('checked')) {
          val = true;
        } else {
          val = false;
        }
        results[key] = val;
        return;
      }
      val = $(this).val();
      return results[key] = val;
    });
    return $.when(start_loading(), post_vimrc(connection_id, results)).done(function() {
      stop_loading();
      return $("#console .terminal").focus();
    });
  });

  post_vimrc = function(id, vimrc_contents) {
    var defer;
    defer = $.Deferred();
    $.ajax({
      type: "POST",
      url: "/api/vimrc",
      contentType: "application/json",
      dataType: "json",
      data: JSON.stringify({
        "connection_id": id,
        "vimrc_contents": vimrc_contents
      }),
      success: function(data) {
        return $.when(vim_reload(), vimrc_reload(id)).done(function() {
          return defer.resolve();
        });
      }
    });
    return defer.promise();
  };

  vim_reload = function() {
    var defer;
    defer = $.Deferred();
    tty.socket.emit('data', terminal_id, "\x1b\x1b:wq\r");
    setTimeout(function() {
      tty.socket.emit('data', terminal_id, "vim\r");
      return setTimeout(function() {
        return defer.resolve();
      }, 500);
    }, 500);
    return defer.promise();
  };

  vimrc_reload = function(connection_id) {
    var defer;
    defer = $.Deferred();
    $.ajax({
      type: "GET",
      url: "/api/vimrc/" + connection_id,
      dataType: "json",
      success: function(data) {
        var vimrc_html;
        vimrc_html = data.vimrc.replace(/(\n|\r)/g, "<br />");
        $("#vimrc-preview p").html(vimrc_html);
        return defer.resolve();
      },
      error: function(data) {
        return alert("Fail to reload vimrc: " + data.message);
      }
    });
    return defer.promise();
  };

  this.start_loading = function() {
    $("#terminal").css("visibility", "hidden");
    return $.blockUI({
      message: '<h1 class="loading-message">now loading...</h1>',
      css: {
        border: 'none',
        padding: '15px',
        backgroundColor: 'transparent',
        '-webkit-border-radius': '10px',
        '-moz-border-radius': '10px',
        opacity: 1,
        color: '#fff'
      }
    });
  };

  this.stop_loading = function() {
    $("#terminal").css("visibility", "visible");
    return $.unblockUI();
  };

  $('.programing-lang').change(function() {
    var ext;
    ext = $(this).val();
    start_loading();
    tty.socket.emit('data', terminal_id, "\x1b\x1b:wq\r");
    return setTimeout(function() {
      tty.socket.emit('data', terminal_id, "export vimfactory_ext=" + ext + "&&vim\r");
      return setTimeout(function() {
        return stop_loading();
      }, 500);
    }, 1000);
  });

}).call(this);
